import { makeStyles, useTheme } from "@mui/styles";

const useStyles = makeStyles((theme) => ({
    root: {
        justifyContent: "center",
        alignItems: "center",
        direction: "rtl",
        backgroundColor: "#EFEFEF",
        [theme.breakpoints.up("sm")]: {
            backgroundColor: "red",
        },
    },
    text: {
        padding: "2%",
        fontSize: "50",
    },
    button: {
        width: "80%",
        height: "100%",
    },
    textItem: {
        display: "flex",
        justifyContent: "center",
        textAlign: "center",
        alignItems: "center",
        height: "30%",
    },
    item: {
        padding: "5%",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        marginBottom: "10%",
        height: "30%",
    },
}));

export default useStyles;
